var http = require('http');

var options = {
  host: 'www.amazon.co.uk',
  path: '/'
};

callback = function(response) {
  var data = '';

  response.on('data', function (dataChunk) {
    data += dataChunk;
  });

  response.on('end', function () {
    console.log(data);
  });
}

http.request(options, callback).end();
